import React, { Component } from "react";
// import Form from 'react-bootstrap/Form'
// import Button from 'react-bootstrap/Button'
// import Row from 'react-bootstrap/Row';
// import Col from 'react-bootstrap/Col';
import axios from "axios";
// import CustomerLogin from './customer-login.component';
import Swal from "sweetalert2";

export default class RegisterCustomer extends Component {
  constructor(props) {
    super(props);

    this.onChangeInput = this.onChangeInput.bind(this);
    this.onSendData = this.onSendData.bind(this);

    this.state = {
      name: "",
      email: "",
      phoneNo: "",
      currentAddress: "",
      password: "",
    };
  }

//   onChangeInput(e) {
//     this.setState({ name: e.target.value });
//     this.setState({ email: e.target.value });
//     this.setState({ phoneNo: e.target.value });
//     this.setState({ currentAddress: e.target.value });
//     this.setState({ password: e.target.value });
//   }

onChangeInput(e){
    this.setState({[e.target.name]:e.target.value});
    console.log(this.state);
}

  onSendData(e) {
    e.preventDefault();
    const customer = {
      name: this.state.name,
      email: this.state.email,
      phoneNo: this.state.phoneNo,
      currentAddress: this.state.currentAddress,
      password: this.state.password,
    };

    axios
      .post("http://localhost:8000/api/addcustomer/", customer)
      // axios.post('https://yupizza-backend.herokuapp.com/api/addcustomer/', customer)
      .then((res) => console.log(res.data));
    Swal.fire("Welcome!", "Account created Successfully", "success");
    this.setState({
      name: "",
      email: "",
      phoneNo: "",
      currentAddress: "",
      password: "",
    });
  }

  render() {
    return (
      <div className="container">
        <br />
        <div className="wrapper">
          <h4 className="fontT">Create account</h4>
          <hr />
        </div>

        <div class="container">
          <form class="col s12" onSubmit={this.onSendData}>
            <div class="row">
              <div class="input-field col s6">
                <input id="name" type="text" class="validate" name="name" onChange={this.onChangeInput} required/>
                <label for="name">Name</label>
              </div>
              <div class="input-field col s6">
                <input id="email" type="email" class="validate" name="email"  onChange={this.onChangeInput} required/>
                <label for="email">Email</label>
              </div>
            </div>
            <div class="row">
              <div class="input-field col s12">
                <input id="address" type="text" class="validate" name="phoneNo"  onChange={this.onChangeInput} required/>
                <label for="address">Address</label>
              </div>              
              <div class="input-field col s12">
                <input id="phone" type="text" class="validate" name="currentAddress" onChange={this.onChangeInput}/>
                <label for="phone">Phone</label>
              </div>
            </div>
            <div class="row">
              <div class="input-field col s12">
                <input id="password" type="password" class="validate" name="password" onChange={this.onChangeInput} required/>
                <label for="password">Password</label>
              </div>
            </div>            
            <div class="row">
              <input type="submit" className="btn btn-primary" value="SignUp" />
            </div>
          </form>
        </div>
      </div>
    );
  }
}
