import React, { Component } from "react";
import axios from "axios";
// import { addToCart } from "./actions/cartActions";
// import { connect } from "react-redux";

// import Row from 'react-bootstrap/Row';

export default class SinglePizza extends Component {
//   handleClick = (id) => {
//     this.props.addToCart(id);
//     console.log(id);
//   };

  constructor(props) {
    super(props);
    this.state = {
      items: [],
    };
  }

  fun() {
    this.props.history.push("/singleI");
  }

  componentDidMount() {
    axios
      .get("http://localhost:8000/api/pizza")
      // axios.get('https://yuitem-backend.herokuapp.com/api/item')
      .then((res) => {
        this.setState({ items: res.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    let item = this.state.items.map((item) => {
      return (
        <div className="card" key={item.id}>
          <br></br>
        
          <small>Idnt: {item.id}</small>
          <div className="card-image">
            <img src={item.imgUrl} alt={item.name} />
            
          </div>

          <br></br>

          <span className="card-title">{item.name}</span>

          {/* <div className="card-content">
            <p>{item.description}</p>

            <br></br>

            <p>
              <b>Price: Kes. {item.price}</b>
            </p>
            <button onClick={this.fun.bind(this.item.id)}>Singlel Item</button>
          </div> */}
        </div>
      );
    });

    return (
      <div className="container">
        <div className="box">{this.state.items.map(item)}</div>
      </div>
    );
  }
}

