import axios from "axios";
import React, { Component } from "react";
import { connect } from "react-redux";
import { addToCart } from "./actions/cartActions";

class Home extends Component {
  handleClick = (id) => {
    this.props.addToCart(id);
  };

  constructor(props) {
    super(props);
    this.state = {
      items: [],
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:8000/api/pizza")
      // axios.get('https://yuitem-backend.herokuapp.com/api/item')
      .then((res) => {
        this.setState({ items: res.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    let itemList = this.state.items.map((item) => {
      return (
        <div className="card" key={item.id}>
          <br />
          <div className="card-image">
            <img src={item.imgUrl} alt={item.name} />
            <span
              to="/"
              className="btn-floating halfway-fab waves-effect waves-light orange"
              onClick={() => {
                this.handleClick(item.id);
              }}
            >
              <i className="material-icons">add</i>
            </span>
            <span className="card-title">{item.name}</span>
          </div>

          <div className="card-content">
            <p>{item.descri}</p>
            <p>
              <b>Price: {item.price}$</b>
            </p>
          </div>
        </div>
      );
    });

    return (
      <div className="container">
        {/* <h3 className="center fontT">Our items</h3> */}
        <div className="box">{itemList}</div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    items: state.items,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    addToCart: (id) => {
      dispatch(addToCart(id));
    },
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Home);
